package library;

import java.util.*;
import java.util.concurrent.atomic.AtomicInteger;

/**
 * Represents a library member who can borrow books.
 */
public final class Member {
    private static final AtomicInteger idGenerator = new AtomicInteger(1000);

    private final int id;                  // unique member ID
    private final String name;
    private final Set<Integer> borrowedBookIds;  // store book IDs only

    public Member(String name) {
        this.id = idGenerator.getAndIncrement();
        this.name = name;
        this.borrowedBookIds = new HashSet<>();
    }

    /* ---------- Getters ---------- */

    public int getId() { return id; }
    public String getName() { return name; }
    public Set<Integer> getBorrowedBookIds() {
        return Collections.unmodifiableSet(borrowedBookIds);
    }

    /* ---------- Member actions ---------- */

    /**
     * Record that this member borrowed a book with the given id.
     */
    void borrowBook(int bookId) {
        if (!borrowedBookIds.add(bookId)) {
            throw new IllegalStateException(
                    "Member " + id + " already borrowed book " + bookId);
        }
    }

    /**
     * Record that this member returned a book with the given id.
     */
    void returnBook(int bookId) {
        if (!borrowedBookIds.remove(bookId)) {
            throw new IllegalStateException(
                    "Member " + id + " never borrowed book " + bookId);
        }
    }

    /* ---------- Object overrides ---------- */

    @Override
    public String toString() {
        return String.format("Member[id=%d, name=\"%s\", borrowed=%s]",
                id, name, borrowedBookIds);
    }

    @Override
    public int hashCode() {
        return Integer.hashCode(id);
    }

    @Override
    public boolean equals(Object obj) {
        return (obj instanceof Member) && ((Member)obj).id == this.id;
    }
}
